import React, { Component } from 'react';

class UselessClock extends Component
{
    constructor()
    {
        super();
        this.state = 
        {
          local: '',
          current: '',
          times: []
        };
    }

    componentDidMount()
    {
      this.setState({ local: Intl.DateTimeFormat().resolvedOptions().timeZone.toString() });
      const apiUri = 'http://worldtimeapi.org/api/timezone/';
      //apiUri.concat(this.state.local);

      fetch(apiUri)
        .then(response =>
            {
                return response.json();
            })
        .then(times => 
            {
                this.setState({times: times});
            }); 
    }

    handleTimeChange(e)
    {
      this.setState({ current: e.target.value })
    }

    render()
    {
      console.log(this.state);
      return(
        <div>
          <form name="SelectTimeZone" onChange={(e) => this.handleTimeChange(e)}>
            <label>
              <p>Scegli il tuo fuso orario</p>
              <select name="TimeZone">
                {this.state.times.map( (time) => {
                  <otion value={time.dst}>{time.timezone}</otion>
                })}
              </select>
            </label>
          </form>
        </div>
      );
    }
}


export default UselessClock;
