import React, { Component } from 'react';

class UselessClock extends Component
{
    constructor()
    {
        super();
        this.state = 
        {
          apiUri: 'http://worldtimeapi.org/api/timezone/',
          local: '',
          current: [],
          times: []
        };
    }

    componentDidMount()
    {
      const localUri = this.state.apiUri.concat(Intl.DateTimeFormat().resolvedOptions().timeZone.toString());

      fetch(this.state.apiUri)
        .then(response =>
            {
                return response.json();
            })
        .then(times => 
            {
                this.setState({times: times});
            }); 
    }

    handleTimeChange(e)
    {
      const currUri = this.state.apiUri.concat(e.target.value.toLowerCase());
      fetch(currUri)
      .then(response =>
          {
              return response.json();
          })
      .then(time => 
          {
              this.setState({current: time});
          });
    }

    render()
    {
      console.log(this.state);
      return(
        <div>
          <h1>A Useless Clock</h1>
          <div id="CurrentTime">
            A <span id="Place">{this.state.current.timezone}</span> sono le <span id="Time">{new Date(this.state.current.unixtime*1000).toString()}</span>
          </div>
          <form name="SelectTimeZone" onChange={(e) => this.handleTimeChange(e)}>
            <label>
              <p>Scegli il tuo fuso orario</p>
              <select name="TimeZone">
                {
                  this.state.times.map( (time) => (
                    <option key={time.toString()} value={time.toString()}>{time.toString()}</option>
                  ))
                }
              </select>
            </label>
          </form>
        </div>
      );
    }
}

export default UselessClock;
