import React, { Component } from 'react';

class UselessClock extends Component
{
    constructor()
    {
        super();
        this.state = 
        {
          local: '',
          current: '',
          times: []
        };
    }

    componentDidMount()
    {
      this.setState({ local: Intl.DateTimeFormat().resolvedOptions().timeZone.toString() });
      const apiUri = 'http://worldtimeapi.org/api/timezone/';
      apiUri.concat(this.state.local);

      fetch(apiUri)
        .then(response =>
            {
                return response.json();
            })
        .then(current => 
            {
                this.setState({ current: current });
            }); 
    }

    render()
    {
      console.log(this.state);
      return(
        <div>
          <p>{this.state.local}</p>
          <p>{this.state.current.datetime}</p>
        </div>
      );
    }
}


export default UselessClock;
